YUI.add("button-group",function(b){function a(c){a.superclass.constructor.apply(this,arguments);}b.extend(a,b.Base,{initializer:function(c){this.buttons=new b.ArrayList();if(c.srcNodes){if(b.Lang.isString(c.srcNodes)){c.srcNodes=b.all(c.srcNodes);}c.buttons=[];c.srcNodes.each(function(e){var d=new b.Button({srcNode:e}).render();c.buttons.push(d);});}if(c.buttons){b.Array.each(c.buttons,function(d){this.addButton(d);},this);}},getButtons:function(){return this.buttons._items;},getSelectedButtons:function(){var c=[],d;d=this.buttons;d.each(function(e){if(e.get("selected")){c.push(e);}});return c;},getSelectedValues:function(){var d,c=[];d=this.getSelectedButtons();b.Array.each(d,function(e){c.push(e.getNode().get("value"));});return c;},addButton:function(c){var d=this.get("type");if(d==="checkbox"){c.set("type","checkbox");c.get("contentBox").on("click",this._onCBButtonClick,this);}else{if(d==="radio"){c.on("click",this._onRadioButtonClick,this);}}this.buttons.add(c);},_onRadioButtonClick:function(d){console.log("radio");var f=d.target;if(!f.get("selected")){var c=this.getSelectedButtons();b.Array.each(c,function(e){e.set("selected",false);});f.set("selected",true);this.fire("selectionChange");}else{}},_onCBButtonClick:function(c){var d=c.target;d.set("selected",!d.get("selected"));this.fire("selectionChange");}},{ATTRS:{type:{writeOnce:"initOnly",value:"radio"}}});a.NAME="buttongroup";b.ButtonGroup=a;},"@VERSION@",{requires:["button-base"]});