YUI.add("datatype-date-math",function(c,b){var a=c.Lang;c.mix(c.namespace("Date"),{isValidDate:function(d){if(a.isDate(d)&&(isFinite(d))&&(d!="Invalid Date")&&!isNaN(d)&&(d!=null)){return true;}else{return false;}},areEqual:function(e,d){return(this.isValidDate(e)&&this.isValidDate(d)&&(e.getTime()==d.getTime()));},isGreater:function(e,d){return(this.isValidDate(e)&&this.isValidDate(d)&&(e.getTime()>d.getTime()));},isGreaterOrEqual:function(e,d){return(this.isValidDate(e)&&this.isValidDate(d)&&(e.getTime()>=d.getTime()));},isInRange:function(f,e,d){return(this.isGreaterOrEqual(f,e)&&this.isGreaterOrEqual(d,f));},addDays:function(e,d){return new Date(e.getTime()+86400000*d);},addMonths:function(g,d){var f=g.getFullYear();var h=g.getMonth()+d;f=Math.floor(f+h/12);h=(h%12+12)%12;var e=new Date(g.getTime());e.setFullYear(f);e.setMonth(h);return e;},addYears:function(g,f){var e=g.getFullYear()+f;var d=new Date(g.getTime());d.setFullYear(e);return d;},listOfDatesInMonth:function(h){if(!this.isValidDate(h)){return[];}var f=this.daysInMonth(h),g=h.getFullYear(),i=h.getMonth(),e=[];for(var d=1;d<=f;d++){e.push(new Date(g,i,d,12,0,0));}return e;},daysInMonth:function(f){if(!this.isValidDate(f)){return 0;}var e=f.getMonth();var g=[31,28,31,30,31,30,31,31,30,31,30,31];if(e!=1){return g[e];}else{var d=f.getFullYear();if(d%400===0){return 29;}else{if(d%100===0){return 28;}else{if(d%4===0){return 29;}else{return 28;}}}}}});c.namespace("DataType");c.DataType.Date=c.Date;},"@VERSION@",{"requires":["yui-base"]});